const request = require('request');
const runConfig = require('run-config')
const tunnelingAgent = require('ssh-proxy-tunneling');
const opts = function (suffixURL, httpMethod, swaggerURL) {
    var opts = {};
    //host-name , port , organization-name should be variable , for now i made it static
    if (swaggerURL) {
        opts.url =  swaggerURL;
    } else {
        opts.url = runConfig.apigee.url+suffixURL;
    }
    opts.agent = tunnelingAgent;
    opts.method = httpMethod;
    opts.auth  = {
        'user': runConfig.apigee.user,
        'pass': runConfig.apigee.pass
    }
    return opts;
};
module.exports = opts;