const PropertiesReader = require('properties-reader');
const path = require('path');
const logger = require('logger')

//To-DO this code we need to change according  to locale request
var properties = PropertiesReader(path.join(__dirname, 'locales', 'en-us-properties.file'));
logger.info('english locale property loaded')

function localeModule() {

}

localeModule.prototype.getProperty = function (propertyName) {
    //send the property name
    return properties.get(propertyName)
}
localeModule.prototype.getAllProperties  = function () {
    //send the property name
    return properties.getAllProperties()
}

module.exports = new localeModule();