const tunnel = require('tunnel');
const runConfig = require('run-config')
const tunnelingAgent = tunnel.httpsOverHttp({
    maxSockets: 15, // Defaults to 5
    proxy: { // Proxy settings
        host: runConfig.webProxy.host, // Defaults to 'localhost'
        port: runConfig.webProxy.port, // Defaults to 80
        // Basic authorization for proxy server if necessary
        proxyAuth: runConfig.webProxy.user+':'+runConfig.webProxy.pass,
        // Header fields for proxy server if necessary
        headers: {
            'User-Agent': 'Node'
        }
    }
});
module.exports = tunnelingAgent;