var clientMessages = require('locale-module');
function validateSwaggerURL() {

}

validateSwaggerURL.prototype.hasValidInfo = function (info) {
    var message = clientMessages.getProperty('swagger.validSwaggerJSOn');
    //need to check swagger jason  for title and description fields. if those are not there , throwing error
    if (info == undefined  ||
        info.title == undefined  || info.title == '' ||
        info.description == undefined || info.description == '') {
        logger.error(message)
        return message
    } else {
        return true;
    }
}

module.exports = new validateSwaggerURL();